$(document).ready(function(e) {


    $.ajax({
        url: 'api/organizations',
        type: 'get',
        datatype: 'json',
        success: function(data){
            var toAppend = '';
            //if(typeof data === 'object'){
                for(var i=0;i<data.length;i++){
                    toAppend += '<option value='+data[i]["id"]+'>'+data[i]['name']+'</option>';
                }
            //}
            console.log("orgs toAppend",toAppend);
            $('#orgs').append(toAppend);
        }
    });

    $("form[ajax=true]").submit(function(e) {

        e.preventDefault();

        var form_data = $(this).serialize();
        var form_url = $(this).attr("action");
        var form_method = $(this).attr("method").toUpperCase();

        var orgId = $("#orgs").val();

        $("#loadingimg").show();

        $.ajax({
            url: form_url+"/"+orgId, 
            type: form_method,       
            data: form_data,     
            cache: false,
            success: function(data){

                // Create simple HTML response
                var response = 
                    "<div>"+
                        "<h2>Created Admin!</h2>"+
                        "<h4>Name</h4><p>"+data.name+"</p>"+
                        "<h4>E-Mail</h4><p>"+data.email+"</p>"+
                        "<h4>ID</h4><p>"+data.id+"</p>"+
                        "<h4>Tags</h4><p>"+data.tags+"</p>"+
                        "<h4>Org Access</h4><p>"+data.orgAccess+"</p>"+
                    "</div>";

                console.log("response: "+JSON.stringify(data));


                $("#result").html(response); 

                $("#loadingimg").hide();
                $( '#createadmin' ).each(function(){
                    this.reset();
                });
            },
            error: function(error){
                $("#result").html(error.responseText);
            }
        });    

    });

});

